/**
 * Server-status controller
 * @constructor
 */
function statusController() {
    if (this.instance.get.like) {
        var status = {};
        for (var key in this.kernel.status()) {
            if (key.indexOf(this.instance.get.like) !== -1) {
                status[key] = this.kernel.status()[key];
            }
        }
        this.instance.end(null, status);
    } else {
        this.instance.end(null, this.kernel.status());
    }
}

/**
 * Exports
 */
exports = module.exports = statusController;