/**
 * Default controller
 * @constructor
 */
function DefaultController() {
    if (this.instance.request.method.toLowerCase() === 'get') {
        this.instance.end(null, this.instance.get);
    } else {
        this.instance.end(null, this.instance.post);
    }
}

/**
 * Export
 */
exports = module.exports = DefaultController;


